    # Combine all categories including the new ones
    all_stopwords = (
        units
        + prefixes
        + generic_technical
        + atlas_generic
        + math_stats
        + document_structure
        + releases
        + years
        + things
        + equation_leftovers
        + version_patterns
        + computing_terms
        + size_speed_units
        + infrastructure_terms
        + status_terms
        + action_terms
        + file_terms
        + time_terms
        + additional_technical
        + pure_numbers
        + build_terms
        + detector_terms
        + doc_patterns
        + time_date
        + numerical_patterns
        + file_paths
        + status_terms
        + file_types
        + other_english_stop_words
        + github_english_stop_words
    )

    # Add common variations (upper/lower/capitalized)
    final_stopwords = set()
    for word in all_stopwords:
        final_stopwords.add(word.lower())
        final_stopwords.add(word.upper())
        final_stopwords.add(word.capitalize())
        # Add common plural forms for relevant words
        if not word.endswith("s") and not word.endswith("S"):
            final_stopwords.add(f"{word.lower()}s")
            final_stopwords.add(f"{word.upper()}s")
            final_stopwords.add(f"{word.capitalize()}s")

    return list(final_stopwords)
