PRINTABLE_ASCII_CHARACTERS = set(string.printable)


def is_printable(s: str) -> bool:
    """
    Determines whether a character is printable/visible.
    """
    return s in PRINTABLE_ASCII_CHARACTERS or s not in UNPRINTABLE_UNICODE_CHARACTERS


def print_ascii(text: str) -> str:
    """
    Transforms the supplied text, filtering non-printable and non-ascii characters.
    """
    return "".join(s for s in text if s in PRINTABLE_ASCII_CHARACTERS)


def is_true(s: str) -> bool:
    """
    Determines whether the string is given true value or false
    """
    return s.strip().upper() in ("1", "TRUE")
